SELECT JOB_ID ||', '|| FIRST_NAME ||', '|| LAST_NAME AS "Trabajadores por puestos" FROM EMPLOYEES;
SELECT FIRST_NAME, LAST_NAME FROM EMPLOYEES WHERE DEPARTMENT_ID=80 AND FIRST_NAME LIKE 'A%';
SELECT LAST_NAME, SALARY FROM EMPLOYEES WHERE SALARY BETWEEN 7500 AND 12000;
SELECT MAX(SALARY) "M�ximo", MIN(SALARY) "M�nimo", SUM(SALARY) "Suma", AVG(SALARY) "Media" FROM EMPLOYEES;
SELECT COUNT(distinct manager_id) AS "Numero de Jefes" from Employees;
SELECT DEPARTMENT_ID AS "Departamento", COUNT (*) AS "Numero de empleados" FROM EMPLOYEES GROUP BY DEPARTMENT_ID;
SELECT FIRST_NAME, HIRE_DATE FROM EMPLOYEES WHERE HIRE_DATE <(SELECT HIRE_DATE FROM EMPLOYEES WHERE LAST_NAME ='Lorentz') ORDER BY HIRE_DATE;
SELECT EMPLOYEE_ID AS "ID del Empleado", First_name AS "Nombre", last_name AS "Apellido", SALARY AS "Salario" FROM employees WHERE SALARY > (SELECT AVG(SALARY) FROM employees) ORDER BY SALARY ASC;
SELECT EMPLOYEE_ID, LAST_NAME FROM employees WHERE department_id IN (SELECT DEPARTMENT_ID FROM employees WHERE LAST_NAME LIKE '%p%');
SELECT location_id ID, street_address Direccion, city Ciudad, state_province Estado, country_name Pais FROM LOCATIONS Natural JOIN countries;
SELECT E.FIST_NAME, E.LAST_NAME, D.DEPARTMENT_NAME FROM EMPLOYEES E JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID WHERE E.DEPARTMENT_ID IN (50,60,80);
SELECT E.LAST_NAME, J.JOB_TITLE, E.EMPLOYEE_ID, D.DEPARTMENT_NAME FROM EMPLOYEES E JOIN JOBS J ON E.JOB_ID = J.JOB_ID JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID WHERE D.LOCATION_ID IN(SELECT LOCATION_ID FROM LOCATIONS WHERE CITY ='Toronto');
SELECT last_name, salary from employees where (salary <6000 or salary >10000) and department_id in (50,80);
SELECT last_name as Apellido from employees where last_name Like '%m%a%';
SELECT job_id, hire_date from employees where last_name in ('Gietz','Jones') order by hire_date desc;
SELECT E.FIRST_NAME AS Nombre, E.LAST_NAME AS Apellido FROM EMPLOYEES E LEFT JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID WHERE E.DEPARTMENT_ID IS NULL OR E.DEPARTMENT_ID IN(SELECT DISTINCT DEPARTMENT_ID FROM EMPLOYEES);